mapper.select:
SELECT number_type AS name, COUNT(*) AS value FROM registration
visit_date >= #{startDate}
AND visit_date <= #{endDate}
GROUP BY number_type

SELECT d.department_name AS name, COUNT(*) AS value FROM registration r JOIN department d ON r.department_id = d.department_id
r.visit_date >= #{startDate}
AND r.visit_date <= #{endDate}
GROUP BY d.department_name

SELECT s.name AS name, COUNT(*) AS value FROM registration r JOIN staff s ON r.doctor_id = s.staff_id
r.visit_date >= #{startDate}
AND r.visit_date <= #{endDate}
GROUP BY s.name
