mapper.select:
(SELECT 'ITEM' AS type, mia.apply_id AS applyId, r.patient_id AS patientId, p.name AS patientName, mia.registration_id AS registrationId, mi.item_id AS itemId, mi.item_code AS itemCode, mi.item_name AS itemName, mi.item_type AS itemType, NULL AS drugCategoryId, mi.price AS price, 1 AS quantity, mi.price AS totalAmount, mi.description AS description, mia.apply_time AS createTime FROM medical_item_apply mia LEFT JOIN registration r ON mia.registration_id = r.registration_id LEFT JOIN patient p ON r.patient_id = p.patient_id LEFT JOIN medical_service_item mi ON mia.item_id = mi.item_id WHERE mia.status = 'PENDING_PAYMENT'
AND ( p.name LIKE CONCAT('%', #{keyword}, '%') OR mi.item_name LIKE CONCAT('%', #{keyword}, '%') )
AND mi.item_type = #{itemType}
) UNION ALL (SELECT 'DRUG' AS type, pr.prescription_id AS applyId, r2.patient_id AS patientId, p2.name AS patientName, pr.registration_id AS registrationId, d.drug_id AS itemId, d.drug_code AS itemCode, d.drug_name AS itemName, NULL AS itemType, d.category_id AS drugCategoryId, d.retail_price AS price, pr.quantity AS quantity, d.retail_price * pr.quantity AS totalAmount, d.description AS description, pr.prescribe_time AS createTime FROM prescription pr LEFT JOIN registration r2 ON pr.registration_id = r2.registration_id LEFT JOIN patient p2 ON r2.patient_id = p2.patient_id LEFT JOIN drug d ON pr.drug_id = d.drug_id WHERE pr.status = 'PENDING_PAYMENT'
AND ( p2.name LIKE CONCAT('%', #{keyword}, '%') OR d.drug_name LIKE CONCAT('%', #{keyword}, '%') )
AND d.category_id = #{drugCategory}
)
SELECT 'ITEM' AS type, mia.apply_id AS applyId, r.patient_id AS patientId, p.name AS patientName, mia.registration_id AS registrationId, mi.item_id AS itemId, mi.item_code AS itemCode, mi.item_name AS itemName, mi.item_type AS itemType, NULL AS drugCategoryId, mi.price AS price, 1 AS quantity, mi.price AS totalAmount, mi.description AS description, mia.apply_time AS createTime FROM medical_item_apply mia LEFT JOIN registration r ON mia.registration_id = r.registration_id LEFT JOIN patient p ON r.patient_id = p.patient_id LEFT JOIN medical_service_item mi ON mia.item_id = mi.item_id WHERE mia.status = 'PENDING_PAYMENT'
AND ( p.name LIKE CONCAT('%', #{keyword}, '%') OR mi.item_name LIKE CONCAT('%', #{keyword}, '%') )
AND mi.item_type = #{itemType}
SELECT 'DRUG' AS type, pr.prescription_id AS applyId, r2.patient_id AS patientId, p2.name AS patientName, pr.registration_id AS registrationId, d.drug_id AS itemId, d.drug_code AS itemCode, d.drug_name AS itemName, NULL AS itemType, d.category_id AS drugCategoryId, d.retail_price AS price, pr.quantity AS quantity, d.retail_price * pr.quantity AS totalAmount, d.description AS description, pr.prescribe_time AS createTime FROM prescription pr LEFT JOIN registration r2 ON pr.registration_id = r2.registration_id LEFT JOIN patient p2 ON r2.patient_id = p2.patient_id LEFT JOIN drug d ON pr.drug_id = d.drug_id WHERE pr.status = 'PENDING_PAYMENT'
AND ( p2.name LIKE CONCAT('%', #{keyword}, '%') OR d.drug_name LIKE CONCAT('%', #{keyword}, '%') )
AND d.category_id = #{drugCategory}
ORDER BY totalAmount
ORDER BY createTime
ORDER BY createTime
ASC
DESC
LIMIT #{pageSize} OFFSET #{offset}

choose.when:
SELECT SUM(cnt) AS total FROM ( SELECT COUNT(1) AS cnt FROM medical_item_apply mia LEFT JOIN registration r ON mia.registration_id = r.registration_id LEFT JOIN patient p ON r.patient_id = p.patient_id LEFT JOIN medical_service_item mi ON mia.item_id = mi.item_id WHERE mia.status = 'PENDING_PAYMENT'
AND ( p.name LIKE CONCAT('%', #{keyword}, '%') OR mi.item_name LIKE CONCAT('%', #{keyword}, '%') )
AND mi.item_type = #{itemType}
UNION ALL SELECT COUNT(1) AS cnt FROM prescription pr LEFT JOIN registration r2 ON pr.registration_id = r2.registration_id LEFT JOIN patient p2 ON r2.patient_id = p2.patient_id LEFT JOIN drug d ON pr.drug_id = d.drug_id WHERE pr.status = 'PENDING_PAYMENT'
AND ( p2.name LIKE CONCAT('%', #{keyword}, '%') OR d.drug_name LIKE CONCAT('%', #{keyword}, '%') )
AND d.category_id = #{drugCategory}
) t

SELECT COUNT(1) FROM medical_item_apply mia LEFT JOIN registration r ON mia.registration_id = r.registration_id LEFT JOIN patient p ON r.patient_id = p.patient_id LEFT JOIN medical_service_item mi ON mia.item_id = mi.item_id WHERE mia.status = 'PENDING_PAYMENT'
AND ( p.name LIKE CONCAT('%', #{keyword}, '%') OR mi.item_name LIKE CONCAT('%', #{keyword}, '%') )
AND mi.item_type = #{itemType}

SELECT COUNT(1) FROM prescription pr LEFT JOIN registration r2 ON pr.registration_id = r2.registration_id LEFT JOIN patient p2 ON r2.patient_id = p2.patient_id LEFT JOIN drug d ON pr.drug_id = d.drug_id WHERE pr.status = 'PENDING_PAYMENT'
AND ( p2.name LIKE CONCAT('%', #{keyword}, '%') OR d.drug_name LIKE CONCAT('%', #{keyword}, '%') )
AND d.category_id = #{drugCategory}

mapper.insert:
INSERT INTO transaction (
    business_type, registration_id, patient_id, amount, transaction_type,
    payment_method_id, transaction_time, cashier_id, remark, settlement_type_id
) VALUES (
             #{businessType}, #{registrationId}, #{patientId}, #{amount}, #{transactionType},
             #{paymentMethodId}, #{transactionTime}, #{cashierId}, #{remark}, #{settlementTypeId}
         )

mapper.update:
UPDATE medical_item_apply
SET status = #{status}
WHERE apply_id = #{applyId}

UPDATE prescription
SET status = #{status}
WHERE prescription_id = #{prescriptionId}

UPDATE patient_visit
SET current_status = #{status}, updated_at = NOW(), status_changed_at = NOW()
WHERE registration_id = #{registrationId}
