mapper.select:
SELECT t.transaction_id AS transactionId, p.name AS patientName, r.registration_id AS registrationId, CASE WHEN t.transaction_type = 'PAY' THEN 'PAID' WHEN t.transaction_type = 'REFUND' THEN 'REFUND' ELSE t.transaction_type END AS status, t.amount AS amount, t.transaction_time AS transactionTime, t.remark AS remark FROM transaction t JOIN registration r ON t.registration_id = r.registration_id JOIN patient p ON r.patient_id = p.patient_id
AND p.name LIKE CONCAT('%', #{name}, '%')
AND ( (#{status} = 'PAID' AND t.transaction_type = 'PAY') OR (#{status} = 'REFUND' AND t.transaction_type = 'REFUND') )
AND t.transaction_time >= #{startTime}
AND t.transaction_time <= #{endTime}
ORDER BY
CONVERT(p.name USING gbk) t.transaction_time
ASC DESC
LIMIT #{offset}, #{pageSize}

SELECT COUNT(*) FROM transaction t JOIN registration r ON t.registration_id = r.registration_id JOIN patient p ON r.patient_id = p.patient_id
AND p.name LIKE CONCAT('%', #{name}, '%')
AND ( (#{status} = 'PAID' AND t.transaction_type = 'PAY') OR (#{status} = 'REFUND' AND t.transaction_type = 'REFUND') )
AND t.transaction_time >= #{startTime}
AND t.transaction_time <= #{endTime}
