mapper.update:
UPDATE drug
SET stock_quantity = stock_quantity - #{quantity}
WHERE drug_id = #{drugId}
  AND stock_quantity >= #{quantity}

UPDATE drug
SET stock_quantity = stock_quantity + #{quantity}
WHERE drug_id = #{drugId}

mapper.select:
SELECT pv.registration_id AS registrationId, p.name, p.patient_no AS medicalNo, pv.current_status AS status, pv.visit_date AS registrationDate, mr.chief_complaint AS complaint FROM patient_visit pv JOIN patient p ON pv.patient_id = p.patient_id LEFT JOIN medical_record mr ON pv.registration_id = mr.registration_id WHERE pv.doctor_id = #{doctorId}
AND ( p.name LIKE CONCAT('%', #{keyword}, '%') OR p.patient_no LIKE CONCAT('%', #{keyword}, '%') )
AND pv.current_status IN
#{status}
AND pv.visit_date = #{visitDate} ORDER BY pv.visit_date DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*) FROM patient_visit pv JOIN patient p ON pv.patient_id = p.patient_id WHERE pv.doctor_id = #{doctorId}
AND ( p.name LIKE CONCAT('%', #{keyword}, '%') OR p.patient_no LIKE CONCAT('%', #{keyword}, '%') )
AND pv.current_status IN
#{status}
AND pv.visit_date = #{visitDate}

SELECT p.name, p.patient_no AS medicalNo, pv.current_status AS status, pv.visit_date AS registrationDate, mr.chief_complaint AS complaint FROM patient_visit pv JOIN patient p ON pv.patient_id = p.patient_id LEFT JOIN medical_record mr ON pv.registration_id = mr.registration_id WHERE pv.doctor_id = #{doctorId}
AND pv.current_status IN
#{status}
AND pv.visit_date = #{visitDate} ORDER BY pv.visit_date DESC

mapper.update:
UPDATE patient_visit
SET current_status = 'FINISHED'
WHERE registration_id = #{registrationId}
