mapper.select:
SELECT p.prescription_id AS prescriptionId, r.registration_id AS registrationId, pa.name AS patientName, pa.gender, pa.patient_no AS patientNo, p.drug_id AS drugId, d.drug_name AS drugName, c.category_name AS drugCategory, d.specification AS specification, d.unit AS unit, p.dosage AS dosage, p.quantity AS quantity, p.prescribe_time AS prescribeTime, p.remark AS remark FROM prescription p JOIN registration r ON p.registration_id = r.registration_id JOIN patient pa ON r.patient_id = pa.patient_id JOIN drug d ON p.drug_id = d.drug_id JOIN drug_category c ON d.category_id = c.category_id WHERE p.status = 'UNFINISHED'
AND ( pa.name LIKE CONCAT('%', #{keyword}, '%') OR d.drug_name LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY r.registration_id DESC, p.prescribe_time DESC LIMIT #{offset}, #{pageSize}

mapper.update:
UPDATE prescription
SET status = 'FINISHED'
WHERE prescription_id = #{prescriptionId}

UPDATE patient_visit
SET current_status = #{status}
WHERE registration_id = #{registrationId}

UPDATE prescription
SET status = 'RETURNED'
WHERE prescription_id = #{prescriptionId}

mapper.insert:
INSERT INTO pharmacy_record (
    prescription_id, patient_no, patient_name, drug_id, drug_name,
    quantity, amount, operate_type, operator_id, operate_time
) VALUES (
             #{prescriptionId}, #{patientNo}, #{patientName}, #{drugId}, #{drugName},
             #{quantity}, #{amount}, #{operateType}, #{operatorId}, #{operateTime}
         )

mapper.update:
UPDATE drug
SET stock_quantity = stock_quantity - #{quantity}
WHERE drug_id = #{drugId}
  AND stock_quantity >= #{quantity}

UPDATE drug
SET stock_quantity = stock_quantity + #{quantity}
WHERE drug_id = #{drugId}

mapper.select:
SELECT COUNT(1) FROM pharmacy_record pr JOIN prescription p ON pr.prescription_id = p.prescription_id JOIN registration r ON p.registration_id = r.registration_id JOIN patient pa ON r.patient_id = pa.patient_id JOIN drug d ON pr.drug_id = d.drug_id
AND pr.operate_type = #{type}
AND (pa.name LIKE CONCAT('%', #{keyword}, '%') OR d.drug_name LIKE CONCAT('%', #{keyword}, '%'))
