mapper.update:
UPDATE prescription
SET status = 'FINISHED'
WHERE prescription_id = #{prescriptionId}

UPDATE patient_visit
SET current_status = #{status}
WHERE registration_id = #{registrationId}

UPDATE prescription
SET status = 'RETURNED'
WHERE prescription_id = #{prescriptionId}

mapper.insert:
INSERT INTO pharmacy_record (
    prescription_id, patient_no, patient_name, drug_id, drug_name,
    quantity, amount, operate_type, operator_id, operate_time
) VALUES (
             #{prescriptionId}, #{patientNo}, #{patientName}, #{drugId}, #{drugName},
             #{quantity}, #{amount}, #{operateType}, #{operatorId}, #{operateTime}
         )

mapper.update:
UPDATE drug
SET stock_quantity = stock_quantity - #{quantity}
WHERE drug_id = #{drugId}
  AND stock_quantity >= #{quantity}

UPDATE drug
SET stock_quantity = stock_quantity + #{quantity}
WHERE drug_id = #{drugId}

mapper.select:
SELECT COUNT(1) FROM pharmacy_record
AND (patient_name LIKE CONCAT('%', #{keyword}, '%') OR drug_name LIKE CONCAT('%', #{keyword}, '%'))
AND operate_type = #{type}
