SELECT item_id AS itemId, item_code AS itemCode, item_name AS itemName, item_type AS itemType, price, description FROM medical_service_item WHERE item_type = 'EXAM' AND status = 1
AND (item_name LIKE CONCAT('%', #{keyword}, '%') OR item_code LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY item_id LIMIT #{pageSize} OFFSET #{offset}

SELECT item_id AS itemId, item_code AS itemCode, item_name AS itemName, item_type AS itemType, price, description FROM medical_service_item WHERE item_type = 'LAB' AND status = 1
AND (item_name LIKE CONCAT('%', #{keyword}, '%') OR item_code LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY item_id LIMIT #{pageSize} OFFSET #{offset}

SELECT item_id AS itemId, item_code AS itemCode, item_name AS itemName, item_type AS itemType, price, description FROM medical_service_item WHERE item_type = 'DISPOSAL' AND status = 1
AND (item_name LIKE CONCAT('%', #{keyword}, '%') OR item_code LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY item_id LIMIT #{pageSize} OFFSET #{offset}

SELECT d.drug_id AS drugId, d.drug_code AS drugCode, d.drug_name AS drugName, d.category_id AS categoryId, c.category_name AS categoryName, c.description AS categoryDescription, d.production_date AS productionDate, d.shelf_life AS shelfLife, d.stock_quantity AS stockQuantity, d.specification AS specification, d.unit AS unit, d.retail_price AS retailPrice, d.description AS description, d.manufacturer AS manufacturer FROM drug d LEFT JOIN drug_category c ON d.category_id = c.category_id
(d.drug_name LIKE CONCAT('%', #{keyword}, '%') OR d.drug_code LIKE CONCAT('%', #{keyword}, '%'))
AND d.category_id = #{categoryId}
ORDER BY d.drug_code LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(*) FROM drug d LEFT JOIN drug_category c ON d.category_id = c.category_id
(d.drug_name LIKE CONCAT('%', #{keyword}, '%') OR d.drug_code LIKE CONCAT('%', #{keyword}, '%'))
AND d.category_id = #{categoryId}
